methods.method:
function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.jxParams.find("n1_img").text()=="" || oCurObj.jxParams.find("n2_img").text()=="" || oCurObj.jxParams.find("n3_img").text()=="")
				{
					return false;
				}
				oCurObj.data =
				{
					sState: "enabled",
					bNaviBtn: (oCurObj.jxParams.find("navibutton").text()=="yes"),
					bNaviStrict: (oCurObj.jxParams.find("nav_lock").text()=="yes"),
					bUseMethod: (oCurObj.jxParams.find("use_object").text()=="yes"),
					bNaviLockedByAction: false,
					sTargetId: oCurObj.jxParams.find("target_object").text(),
					sSoundClick: oCurObj.jxParams.children("sound_click").text(),
					sSoundOver: oCurObj.jxParams.children("sound_over").text()
				};
				if(oCurObj.data.sSoundClick=="custom")
				{
					oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
				}
				if(oCurObj.data.sSoundOver=="custom")
				{
					oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
				}
				var jEventDiv = jCurObjDiv.children(".cl-wrapper");
				if(jEventDiv.length!=0)
				{
					jEventDiv[0].removeAttribute("style");
					jEventDiv[0].setAttribute("data-parent-id", argobj.pid);
					var sBody;
					var sNewFn;
					sBody = jEventDiv[0].getAttribute("onclick");
					if(sBody!=null && sBody!="")
					{
						jEventDiv[0].setAttribute("onclick", 'if(CLO[this.getAttribute("data-parent-id")].data.sState=="enabled") {' + sBody + '}');
					}
					sBody = jEventDiv[0].getAttribute("ondblclick");
					if(sBody!=null && sBody!="")
					{
						jEventDiv[0].setAttribute("ondblclick", 'if(CLO[this.getAttribute("data-parent-id")].data.sState=="enabled") {' + sBody + '}');
					}
					sBody = jEventDiv[0].getAttribute("onmousedown");
					if(sBody!=null && sBody!="")
					{
						jEventDiv[0].setAttribute("onmousedown", 'if(CLO[this.getAttribute("data-parent-id")].data.sState=="enabled") {' + sBody + '}');
					}
					sBody = jEventDiv[0].getAttribute("onmouseup");
					if(sBody!=null && sBody!="")
					{
						jEventDiv[0].setAttribute("onmouseup", 'if(CLO[this.getAttribute("data-parent-id")].data.sState=="enabled") {' + sBody + '}');
					}
				}
				if(CL.bTouch)
				{
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				else
				{
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("mouseover", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "over" } }) }).on("mouseout", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "out" } }) }).on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				if(oCurObj.data.bNaviBtn)
				{
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
					if(CLO[oCurObj.data.sTargetId]!=null)
					{
						if(CLO[oCurObj.data.sTargetId].data!=null)
						{
							if(CLO[oCurObj.data.sTargetId].data.sRole!=null)
							{
								oCurObj.data.sRole = CLO[oCurObj.data.sTargetId].data.sRole;
							}
						}
					}
				}
				return true;
			}

function HandleEvt(oEvtHandler, oArg)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null)
				{
					return false;
				}
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				switch(argobj.evt)
				{
					case "EVENT_SLIDE_OPENED":
					{
						if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
						{
							oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
							oCurObj.data.bNaviLockedByAction = false;
						}
						oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
						break;
					}
					case "EVENT_SLIDE_COMPLETE":
					{
						oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
						break;
					}
					case "EVENT_NAVIGATION":
					{
						if(argobj.sTargets!=null)
						{
							if(argobj.sTargets=="all" || argobj.sTargets=="tabs" || (argobj.sTargets=="contents" && oCurObj.data.sRole=="contents"))
							{
								switch(argobj.sAction)
								{
									case "off":
									{
										if(!oCurObj.data.bNaviLockedByAction)
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
										}
										break;
									}
									case "on":
									default:
									{
										oCurObj.data.bNaviLockedByAction = false;
										oCurObj.CallMethod({ sMethod: "EnableAccess" });
										break;
									}
								}
							}
						}
						break;
					}					
				}
				return true;
			}

function SetState(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jBtn = jCurObjDiv.find(".cl-btn");
				if(jBtn.hasClass("cl-disabled") && argobj.sState!="enabled") return false;
				switch(argobj.sState)
				{
					case "over":
					{
						jBtn.removeClass("cl-idle").addClass("cl-over");
						CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
						if(oCurObj.data.sSoundOver!="none")
						{
							CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
						}
						break;
					}
					case "out":
					{
						jBtn.removeClass("cl-over").addClass("cl-idle");
						break;
					}
					case "disabled":
					{
						jBtn.removeClass("cl-idle cl-over").addClass("cl-disabled");
						CLO[argobj.pid].data.sState = "disabled";
						if(argobj.bWindow==true)
						{
							oCurObj.data.bNaviLockedByAction = true;
						}
						break;
					}
					case "enabled":
					{
						jBtn.removeClass("cl-disabled").addClass("cl-idle");
						CLO[argobj.pid].data.sState = "enabled";
						if(argobj.bWindow==true)
						{
							oCurObj.data.bNaviLockedByAction = false;
						}
						break;
					}
				}
				return true;
			}

function DisableAccess(argobj)
			{
				CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
				return true;
			}

function EnableAccess(argobj)
			{
				CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "enabled" } });
				return true;
			}

function GetProperty(argobj)
			{
				switch(argobj.property.toLowerCase())
				{
					case "disabled":
					{
						return (CLO[argobj.pid].data.sState=="disabled" ? "1" : "0");
					}
				}
				return "";
			}

function Click(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.sState=="disabled") return false;
				if(oCurObj.data.bUseMethod)
				{
					var sObjectId = oCurObj.jxParams.find("target_object").text();
					if(sObjectId==null || sObjectId=="") return false;
					if(CLO[sObjectId]==null)
					{
						CLO[sObjectId] = new CLObject({ xNode: CL.axSlides.find("object[id='" + sObjectId + "']") });
					}
					if(CLO[sObjectId].oMethods["Open"]!=null && CLO[sObjectId].oMethods["Close"]!=null)
					{
						if(CLO[sObjectId].bOpen==true)
						{
							CLO[sObjectId].CallMethod({ sMethod: "Close" });
						}
						else
						{
							CLO[sObjectId].CallMethod({ sMethod: "Open" });
						}
					}
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				oCurObj.data = { bActive: false, sHLType: oCurObj.jxParams.find("highlight_type").text(), nDuration: +oCurObj.jxParams.find("timer").text() };
				jCurObjDiv.css({ "cursor": "default" });
				switch(oCurObj.data.sHLType)
				{
					case "blink":
					{
						oCurObj.HLOn = function ()
						{
							if(oCurObj.data.bActive)
							{
								jCurObjDiv.show({ effect: "fade", duration: 100 });
								oCurObj.iHLTimer = setTimeout(function () { oCurObj.HLOff(); }, oCurObj.data.nDuration);
							}
						};
						oCurObj.HLOff = function ()
						{
							if(oCurObj.data.bActive)
							{
								jCurObjDiv.hide({ effect: "fade", duration: 100 });
								oCurObj.iHLTimer = setTimeout(function () { oCurObj.HLOn(); }, oCurObj.data.nDuration);
							}
						};
						break;
					}
					case "pulse":
					{
						oCurObj.HLOn = function ()
						{
							if(oCurObj.data.bActive)
							{
								var iX = Math.floor(0.03*oCurObj.current.w);
								var iY = Math.floor(0.03*oCurObj.current.h);
								jCurObjDiv.find(".cl-highlight").animate({ "left": -iX + "px", "top": -iY + "px", "zoom": 1.06 }, 100);
								oCurObj.iHLTimer = setTimeout(function () { oCurObj.HLOff(); }, oCurObj.data.nDuration);
							}
						};
						oCurObj.HLOff = function ()
						{
							if(oCurObj.data.bActive)
							{
								jCurObjDiv.find(".cl-highlight").css({ "zoom": 1.0, "left": "0", "top": "0" });
								oCurObj.iHLTimer = setTimeout(function () { oCurObj.HLOn(); }, oCurObj.data.nDuration);
							}
						};
						break;
					}
					case "none":
					{
						oCurObj.HLOn = function ()
						{
							if(oCurObj.data.bActive)
							{
								jCurObjDiv.show();
								oCurObj.iHLTimer = setTimeout(function () { oCurObj.HLOff(); }, oCurObj.data.nDuration);
							}
						};
						oCurObj.HLOff = function ()
						{
							if(oCurObj.data.bActive)
							{
								jCurObjDiv.hide();
								oCurObj.iHLTimer = setTimeout(function () { oCurObj.HLOn(); }, oCurObj.data.nDuration);
							}
						};
						break;
					}
				}
				return true;
			}

function HighlightOn(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.data.bActive) return false;
				jCurObjDiv.find(".cl-empty").css({ "display": "none" });
				jCurObjDiv.find(".cl-highlight").css({ "display": "block" });
				oCurObj.data.bActive = true;
				oCurObj.HLOn();
				return true;
			}

function HighlightOff(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(!oCurObj.data.bActive) return false;
				jCurObjDiv.find(".cl-empty").css({ "display": "block" });
				jCurObjDiv.find(".cl-highlight").css({ "display": "none" });
				oCurObj.data.bActive = false;
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var bDefer = false; 
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}
				if(oCurObj.jxParams.children("hide_on_click").text()=="yes")
				{
					jCurObjDiv.find(".cl-btn-close-object").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
				}
				oCurObj.data =
				{
					bCreated: true,
					bTypeWriter: (oCurObj.jxParams.children("typewriter").text()=="yes")
				};
				if(oCurObj.data.bTypeWriter)
				{
					var sSpeed = oCurObj.jxParams.children("typewriter_speed").text();
					var iSpeed = (sSpeed=="") ? 10 : parseInt(sSpeed, 10);
					if(isNaN(iSpeed)) iSpeed = 10;
					oCurObj.data.iDelay = Math.round(1000/iSpeed);
				}
				var sHTML;
				for(var sState in oCurObj.oStates)
				{
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
					sHTML = CL.Substitute.All({ sString: oCurObj.oStates[sState].sText });
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-txt-box-cell").html( sHTML );
				}
				if(CL.bTouch)
				{
					oCurObj.data.jTextBox.on("touchmove", function(e) { e.stopPropagation() }).addClass("cl-touch-scroll");
				}
				if(!bDefer)
				{
					oCurObj.CallMethod({ sMethod: "Display" });
				}
				else
				{
					jCurObjDiv.find(".cl-container:first").hide();
				}
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj==null || jCurObjDiv==null) return false; 
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.CallMethod({ sMethod: "Display" });
						break;
					}
				}
				return true;
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				jCurObjDiv.find(".cl-container:first").show();
				if(oCurObj.data.bTypeWriter)
				{
					oCurObj.CallMethod({ sMethod: "TypeWriter", oMethodArgs: { oElem: jCurObjDiv.find("div[data-state='default'] .cl-txt-box-cell"), iDelay: oCurObj.data.iDelay, iItem: 0 } });
				}
				return true;
			}

function TypeWriter(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem = $(argobj.oElem); 
				if(jElem.attr("data-wrapped")=="yes")
				{
					jElem.find(".cl-char").each(function () { $(this).contents().unwrap() });
				}
				jElem.attr({ "data-wrapped": "yes" }).html( CL.Common.WrapCharacters({ sHTML: jElem.html() }) );
				oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: jElem, iDelay: oCurObj.data.iDelay, iItem: 0 } });
				return true;
			}

function NextChar(argobj)
			{
				if(argobj==null) return false;
				if(argobj.pid==null || argobj.oElem==null || argobj.iDelay==null || argobj.iItem==null)
				{
					return false;
				}
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null)
				{
					return false;
				}
				var jElem = $(argobj.oElem);
				var jItem = jElem.find(".cl-char[data-i='" + argobj.iItem + "']");
				if(jItem.length==0)
				{
					oCurObj.FireEvent({ sName: "onTypeWriterEnd" });
					return false;
				}
				jItem.contents().unwrap();
				oCurObj.iTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: argobj.oElem, iDelay: argobj.iDelay, iItem: (argobj.iItem+1) } }); }, argobj.iDelay);
				return true;
			}

function UIEvent(argobj)
			{
				if(argobj.evt==null) return false;
				var jElem = $(argobj.elem);
				switch(argobj.evt.type)
				{
					case "click":
					{
						CLO[argobj.pid].CallMethod({ sMethod: "Close" });
						break;
					}
				}
				return true;
			}

function Close(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				oCurObj.Hide();
				oCurObj.FireEvent({ sName: "onCloseObject" });
				return true;
			}

function RefreshText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				for(var sState in oCurObj.oStates)
				{
					jState = jCurObjDiv.find("div[data-state='" + sState + "']");
					jState.find(".cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onRefresh" });
				return true;
			}

function ResetText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				for(var sState in oCurObj.oStates)
				{
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
					jState = jCurObjDiv.find("div[data-state='" + sState + "']");
					jState.find(".cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onReset" });
				return true;
			}

function AddText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				var sText;
				for(var sState in oCurObj.oStates)
				{
					sText = CL.Common.AddText({ sHTML: oCurObj.oStates[sState].sText, sText: argobj.value, sWhere: argobj.where, sHow: argobj.how });
					oCurObj.oStates[sState].sText = sText;
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onAddText" });
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return "";
				if(CLO[argobj.pid]==null) return "";
				switch(argobj.property.toLowerCase())
				{
					case "state":
					{
						return oCurObj.sCurrentState;
					}
				}
				return "";
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				oCurObj.data =
				{
					bAutoStart: (oCurObj.jxParams.find("video_autostart").text()=="yes"),
					bControls: (oCurObj.jxParams.find("video_controls").text()!="none"),
					bInvisible: (oCurObj.jxParams.find("video_invisible").text()=="yes"),
					sPlayer: jCurObjDiv.find(".cl-container").attr("data-player"),
					sUIMode: oCurObj.jxParams.find("video_controls").text()
				};
				if(oCurObj.data.sUIMode=="no") oCurObj.data.sUIMode = "none";
				var sPath = (oCurObj.jxParams.find("source").text()=="url") ? oCurObj.jxParams.find("video_url").text() : oCurObj.jxParams.find("video_uri").text();
				var sURL = document.location.href;
				var iPtr = sURL.indexOf("?");
				if(iPtr!=-1) sURL = sURL.substr(0,iPtr);
				iPtr = sURL.lastIndexOf("/");
				sURL = sURL.substr(0,iPtr+1);
				var bEmbed = false;
				var bFF = false;
				var oPlayer;
				var oParent;
				var sPlayerID;
				var jContainer = jCurObjDiv.find(".cl-container");
				switch(oCurObj.data.sPlayer)
				{
					case "html5":
					{
						var jPlayer = jCurObjDiv.find("video");
						jPlayer[0].onplay = function (e)
						{
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayBegin" });
						}
						jPlayer[0].onplaying = function (e)
						{
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayResume" });
						}
						jPlayer[0].onpause = function (e)
						{
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayPause" });
						}
						jPlayer[0].onseeked = function (e)
						{
							oCurObj.FireEvent({ sName: "onPositionChange" });
						}
						jPlayer[0].onended = function (e)
						{
							oCurObj.FireEvent({ sName: "onMediaEnded" });
						}
						break;
					}
					case "flv":
					{
						var sPlayer = (oCurObj.data.bControls) ? "images/media_003_video/player1.swf" : "images/media_003_video/player0.swf";
						var sFile = sPath;
						var reImages = new RegExp(/images/);
						sFile = sFile.replace(reImages,"..");
						var oParams = { "quality": "high", "wmode": "transparent" };
						oParams.flashvars = "objID=" + argobj.pid + "&objtype=media_003_video&file=" + escape(sFile) + "&objwidth=" + oCurObj.initial.w + "&objheight=" + oCurObj.initial.h + "&fit=false&autorewind=true&autostart=" + (oCurObj.data.bAutoStart ? "true" : "false");
						var oFlash = new CLFlash(
						{
							container: jContainer,
							sPath: sPlayer,
							nWidth: oCurObj.initial.w,
							nHeight: oCurObj.initial.h,
							oParams: oParams
						});
						break;
					}
					case "qtp":
					{
						break;
					}
					case "wmp":
					{
						break;
					}
				}
				if(oCurObj.data.bAutoStart)
				{
					if(oCurObj.data.sPlayer!="flv") oCurObj.iCheckTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Tick" }); }, 1000);
				}
				return true;
			}

function EventHandler(argobj)
			{
				return true;
			}

function SetProperty(argobj)
			{
				if(argobj.property==null || argobj.value==null) return false;
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jPlayer;
				switch(oCurObj.data.sPlayer)
				{
					case "html5":
					{
						jPlayer = jCurObjDiv.find("video");
						if(jPlayer.length>0)
						{
							switch(argobj.property)
							{
								case "currentposition":
								{
									var iTime = parseInt(argobj.value, 10);
									if(!isNaN(iTime))
									{
										jPlayer[0].currentTime = iTime;
									}
									break;
								}
								case "volume":
								{
									var iVolume = parseInt(argobj.value, 10);
									if(!isNaN(iVolume))
									{
										if(iVolume<0) iVolume = 0;
										if(iVolume>100) iVolume = 100;
										jPlayer[0].volume = 0.01*iVolume;
									}
									break;
								}
								case "mute":
								{
									var iValue = parseInt(argobj.value, 10);
									if(!isNaN(iValue))
									{
										if(iValue<0) iValue = 0;
										if(iValue>1) iValue = 1;
										jPlayer[0].muted = (iValue>0);
									}
									break;
								}
							}
						}
						break;
					}
					case "wmp":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							try
							{
								var oControls = jPlayer[0].controls;
								var oSettings = jPlayer[0].settings;
								switch(argobj.property)
								{
									case "mute":
									{
										if(oSettings.mute!=null) oSettings.mute = (argobj.value==1 || argobj.value=="1");
										break;
									}
									case "volume":
									{
										var iVolume = parseInt(argobj.value,10);
										if(isNaN(iVolume)) return false;
										if(iVolume<0) iVolume = 0;
										if(iVolume>100) iVolume = 100; 
										if(oSettings.volume!=null) oSettings.volume = iVolume.toString();	
										break;
									}
									case "currentposition":
									{
										var nCurPos = parseFloat(argobj.value);
										if(isNaN(nCurPos)) return false;
										oControls.currentPosition = nCurPos;
										oCurObj.FireEvent({ sName: "onPositionChange" });
										break;
									}
								}
							}
							catch(e)
							{}
						}
						break;
					}
					case "qtp":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							switch(argobj.property)
							{
								case "mute":
								{
									jPlayer[0].SetMute((argobj.value.toString()=="1") ? true : false);
									break;
								}
								case "volume":
								{
									var iVolume = parseInt(argobj.value, 10);
									if(!isNaN(iVolume))
									{
										if(iVolume<0) iVolume = 0;
										if(iVolume>100) iVolume = 100;
										jPlayer[0].SetVolume(Math.round(iVolume*2.55));
									}
									break;
								}
								case "currentposition":
								{
									var iTime = parseInt(argobj.value, 10);
									if(!isNaN(iTime))
									{
										var iDuration = jPlayer[0].GetDuration();
										var iScale = jPlayer[0].GetTimeScale();
										iTime = Math.floor(iTime*iScale);
										if(iTime>=iDuration) iTime = iDuration;
										if(iTime<=0) iTime = 0;
										jPlayer[0].SetTime(iTime);
										oCurObj.FireEvent({ sName: "onPositionChange" });
									}
									break;
								}
							}
						}
						break;
					}
					case "flv":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							switch(argobj.property)
							{
								case "currentposition":
								{
									var iTime = parseInt(argobj.value, 10);
									if(!isNaN(iTime))
									{
										try
										{
											jPlayer[0].FLVAction("position", iTime.toString());
										}
										catch(x1)
										{
											try
											{
												jPlayer[0].CallFunction("<invoke name=\"FLVAction\" returntype=\"string\"><arguments><string>position</string><string>" + iTime.toString() + "</string></arguments></invoke>");
											}
											catch(x2)
											{
											}
										}
									}
									oCurObj.FireEvent({ sName: "onPositionChange" });
									break;
								}
								case "volume":
								{
									var iVolume = parseInt(argobj.value, 10);
									if(!isNaN(iVolume))
									{
										if(iVolume<0) iVolume = 0;
										if(iVolume>100) iVolume = 100;
										try
										{
											jPlayer[0].FLVAction("volume", iVolume.toString());
										}
										catch(x1)
										{
											try
											{
												jPlayer[0].CallFunction("<invoke name=\"FLVAction\" returntype=\"string\"><arguments><string>volume</string><string>" + iVolume.toString() + "</string></arguments></invoke>");
											}
											catch(x2)
											{
											}
										}
									}
									break;
								}
								case "mute":
								{
									var iValue = parseInt(argobj.value, 10);
									if(!isNaN(iValue))
									{
										if(iValue<0) iValue = 0;
										if(iValue>1) iValue = 1;
										var sAction = (iValue>0) ? "mute" : "unmute";
										try
										{
											jPlayer[0].FLVAction(sAction, "");
										}
										catch(x1)
										{
											try
											{
												jPlayer[0].CallFunction("<invoke name=\"FLVAction\" returntype=\"string\"><arguments><string>" + sAction + "</string><string></string></arguments></invoke>");
											}
											catch(x2)
											{
											}
										}
									}
									break;
								}
							}
						}
						break;
					}
				}
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jPlayer;
				var aPlayStates = ["undefined", "stopped", "paused", "playing", "forward", "backward", "buffering", "waiting", "ended", "preparing", "ready", "reconnect"];
				switch(oCurObj.data.sPlayer)
				{
					case "html5":
					{
						jPlayer = jCurObjDiv.find("video");
						if(jPlayer.length>0)
						{
							switch(argobj.property.toLowerCase())
							{
								case "autostart":
								{
									return (jPlayer[0].autoplay ? "1" : "0");
								}
								case "mute":
								{
									return (jPlayer[0].muted ? "1" : "0");
								}
								case "volume":
								{
									return (100*Player[0].volume);
								}
								case "currentmedia":
								{
									return jPlayer[0].currentSrc;
								}
								case "duration":
								{
									return jPlayer[0].duration;
								}
								case "currentposition":
								{
									return Math.round(jPlayer[0].currentTime);
								}
								case "currentstate":
								{
									var sState = aPlayStates[0];
									if(jPlayer[0].ended)
									{
										sState = "ended";
									}
									else if(jPlayer[0].paused)
									{
										sState = "paused";
									}
									else if(!jPlayer[0].paused)
									{
										sState = "playing";
									}
									return sState;
								}
								case "isonline":
								{
									return (jPlayer[0].networkState!=0 ? "1" : "0");
								}
								case "url":
								{
									var sURL = jPlayer[0].currentSrc;
									var reSlash = new RegExp(/\//g);
									sURL = sURL.replace(reSlash,"\\/");
									return sURL;
								}
							}
						}
						break;
					}
					case "wmp":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							try
							{
								var oControls = jPlayer[0].controls;
								var oMedia = jPlayer[0].currentMedia;
								var oSettings = jPlayer[0].settings;
								switch(argobj.property.toLowerCase())
								{
									case "autostart":
									{
										return (oSettings.autoStart ? "1" : "0");
									}
									case "mute":
									{
										return (oSettings.mute ? "1" : "0");
									}
									case "volume":
									{
										return oSettings.volume;
									}
									case "currentmedia":
									{
										return oMedia.name;
									}
									case "duration":
									{
										return oMedia.duration;
									}
									case "currentposition":
									{
										var sPos = oControls.currentPosition;
										var nPos = parseFloat(sPos);
										var iPos = Math.floor(nPos);
										return iPos.toString();
									}
									case "currentstate":
									{
										var sState = oPlayer.playState;
										if(sState==null) sState = 0;
										var iState = parseInt(sState, 10);
										if(isNaN(iState)) iState = 0;
										return aPlayStates[iState];
									}
									case "isonline":
									{
										return (oPlayer.isOnline ? "1" : "0");
									}
									case "url":
									{
										var sURL = oPlayer.URL;
										var reSlash = new RegExp(/\//g);
										sURL = sURL.replace(reSlash,"\\/");
										return sURL;
									}
								}
							}
							catch(e)
							{}
						}
						break;
					}
					case "qtp":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							try
							{
								switch(argobj.property.toLowerCase())
								{
									case "autostart":
									{
										return (jPlayer[0].GetAutoPlay() ? "1" : "0");
									}
									case "mute":
									{
										return (jPlayer[0].GetMute() ? "1" : "0");
									}
									case "volume":
									{
										var iVolume = jPlayer[0].GetVolume();
										iVolume = Math.round(iVolume/255);
										return iVolume;
									}
									case "currentmedia":
									{
										return jPlayer[0].GetMovieName();
									}
									case "duration":
									{
										var iDuration = jPlayer[0].GetDuration();
										var iScale = oPlayer.GetTimeScale();
										return Math.round(iDuration/iScale*10)/10;
									}
									case "currentposition":
									{
										var iTime = jPlayer[0].GetTime();
										var iScale = jPlayer[0].GetTimeScale();
										return Math.round(iTime/iScale*10)/10;
									}
									case "url":
									{
										var sURL = jPlayer[0].GetURL();
										var reSlash = new RegExp(/\//g);
										sURL = sURL.replace(reSlash,"\\/");
										return sURL;
									}
								}
							}
							catch(e)
							{}
						}
						break;
					}
					case "flv":
					{
						jPlayer = jCurObjDiv.find("object");
						var sValue = "";
						if(jPlayer.length>0)
						{
							try
							{
								sValue = oFPlayer.GetProp(argobj.property.toLowerCase());
							}
							catch(x1)
							{
								try
								{
									sValue = oFPlayer.CallFunction("<invoke name=\"GetProp\" returntype=\"string\"><arguments><string>" + argobj.property.toLowerCase() + "</string></arguments></invoke>");
									if(sValue.indexOf("<string>")!=-1 && sValue.indexOf("</string>")!=-1)
									{
										sValue = sValue.substring(8, sValue.indexOf("</string>"));
									}
								}
								catch(x2)
								{
								}
							}
						}
						return sValue;;
					}
				}
				return "";
			}

function Play(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jPlayer;
				switch(oCurObj.data.sPlayer)
				{
					case "html5":
					{
						jPlayer = jCurObjDiv.find("video");
						if(jPlayer.length>0)
						{
							jPlayer[0].play();
							oCurObj.iCheckTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Tick" }); }, 1000);
						}
						break;
					}
					case "wmp":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							jPlayer[0].controls.play();
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayBegin" });
							oCurObj.iCheckTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Tick" }); }, 1000);
						}
						break;
					}
					case "qtp":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							jPlayer[0].play();
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayBegin" });
							oCurObj.iCheckTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Tick" }); }, 1000);
						}
						break;
					}
					case "flv":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							try
							{
								jPlayer[0].FLVAction("play", "");
							}
							catch(x1)
							{
								try
								{
									jPlayer[0].CallFunction("<invoke name=\"FLVAction\" returntype=\"string\"><arguments><string>play</string><string></string></arguments></invoke>");
								}
								catch(x2)
								{
								}
							}
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayBegin" });
						}
						break;
					}
				}
				return true;
			}

function Stop(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jPlayer;
				switch(oCurObj.data.sPlayer)
				{
					case "html5":
					{
						jPlayer = jCurObjDiv.find("video");
						if(jPlayer.length>0)
						{
							jPlayer[0].pause();
							jPlayer[0].currentTime = 0;
							//oCurObj.FireEvent({ sName: "onChangePlayState" });
							//oCurObj.FireEvent({ sName: "onPlayStop" });
							try
							{
								clearTimeout(oCurObj.iCheckTimer);
							}
							catch(e)
							{}
						}
						break;
					}
					case "wmp":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							jPlayer[0].controls.stop();
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayStop" });
							try
							{
								clearTimeout(oCurObj.iCheckTimer);
							}
							catch(e)
							{}
						}
						break;
					}
					case "qtp":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							jPlayer[0].stop();
							jPlayer[0].SetTime(0);
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayStop" });
							try
							{
								clearTimeout(oCurObj.iCheckTimer);
							}
							catch(e)
							{}
						}
						break;
					}
					case "flv":
					{
						jPlayer = jCurObjDiv.find("object");
						if(jPlayer.length>0)
						{
							try
							{
								jPlayer[0].FLVAction("pause", "");
							}
							catch(x1)
							{
								try
								{
									jPlayer[0].CallFunction("<invoke name=\"FLVAction\" returntype=\"string\"><arguments><string>pause</string><string></string></arguments></invoke>");
								}
								catch(x2)
								{
								}
							}
							oCurObj.FireEvent({ sName: "onChangePlayState" });
							oCurObj.FireEvent({ sName: "onPlayStop" });
						}
						break;
					}
				}
				return true;
			}

